import React, { useState, useEffect } from "react";
import { Bar } from "react-chartjs-2";

const Bar_charts = () => {
  const [barData, setBarData] = useState({});

  const chart = () => {
    setBarData({
      type: "horizontalBar",
      labels: [
        "Branch 1",
        "Branch 2",
        "Branch 3",
        "Branch 4",
        "Branch 5",
        "Branch 6",
        "Branch 7",
        "Branch 8",
        "Branch 9",
        "Branch 10",
        "Branch 11",
        "Branch 12",
        "Branch 13",
        "Branch 14",
        "Branch 15",
        "Branch 16",
        "Branch 17",
        "Branch 10",
      ],
      datasets: [
        {
          label: "Sale In 2020",
          barPercentage: 30,
          barThickness: 30,
          maxBarThickness: 30,
          minBarLength: 30,
          data: [0, 20, 30, 20],
          backgroundColor: [
            "rgba(255, 99, 132,)",
            "rgba(54, 162, 235, 0.2)",
            "rgba(255, 206, 86, 0.2)",
            "rgba(75, 192, 192, 0.2)",
            "rgba(153, 102, 255, 0.2)",
            "rgba(255, 159, 64, 0.2)",
          ],
          borderColor: [
            "rgba(255, 99, 132, 1)",
            "rgba(54, 162, 235, 1)",
            "rgba(255, 206, 86, 1)",
            "rgba(75, 192, 192, 1)",
            "rgba(153, 102, 255, 1)",
            "rgba(255, 159, 64, 1)",
          ],
        },
      ],
    });
  };

  useEffect(() => {
    chart();
  }, []);

  return (
    <div className="col-sm-12 col-md-12 col-lg-12">
      <Bar data={barData} options={{ responsive: true }} />
    </div>
  );
};
export default Bar_charts;
