import React, { useState, useEffect } from "react";

import { Radar } from "react-chartjs-2";
const Radar_charts = () => {
    const [radarData, setRadarData] = useState({});

    const Radar = () => {

        setChartData({
            labels: [
              "Sunday",
              "Monday",
              "Tuesday",
              "Wednesday",
              "Thursday",
              "Friday",
              "Saturday",
            ],



        type: 'radar',
        data: {
            datasets: [{
                label: 'First dataset',
                data: [0, 20, 40, 50]
            }],
            labels: ['January', 'February', 'March', 'April']
        },
        options: {
            scale: {
                ticks: {
                    suggestedMin: 50,
                    suggestedMax: 100
                }
            }
        }
    });

  }
 
  return (
    <div>
      <div className="col-sm-12 col-md-12 col-lg-12">
        <Radar data={radarData} options={{ responsive: true }} />
      </div>
    </div>
  );
};

export default Radar_charts;
