import React, { useState, useEffect } from "react";

const Bar_charts = () => {
  const [barData, setBarData] = useState({});

  const Chart = () => {
    setBarData({
      datasets: [
        {
          label: "Week",
          data: [12, 19, 3, 5, 2, 3],
          backgroundColor: [
            "rgba(255, 99, 132, 0.2)",
            "rgba(54, 162, 235, 0.2)",
            "rgba(255, 206, 86, 0.2)",
            "rgba(75, 192, 192, 0.2)",
            "rgba(153, 102, 255, 0.2)",
            "rgba(255, 159, 64, 0.2)",
          ],
          borderColor: [
            "rgba(255, 99, 132, 1)",
            "rgba(54, 162, 235, 1)",
            "rgba(255, 206, 86, 1)",
            "rgba(75, 192, 192, 1)",
            "rgba(153, 102, 255, 1)",
            "rgba(255, 159, 64, 1)",
          ],
          borderWidth: 1,
        },
      ],
      labels: ["Red", "Blue", "Yellow", "Green", "Purple", "Orange"],
    });
  };

  return (
    <div className="col-sm-12 col-md-12 col-lg-12">
      <bar data={barData} options={{ responsive: true }} />
    </div>
  );
};

export default Bar_charts;
